import React, { useState } from 'react';

const Faqs = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleAccordion = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Frequently Asked Questions</h1>
      <div style={styles.accordion}>
        {/* Accordion Item 1 */}
        <div style={styles.accordionItem}>
          <button
            style={styles.accordionButton}
            onClick={() => toggleAccordion(0)}
          >
            General Questions
          </button>
          {activeIndex === 0 && (
            <div style={styles.accordionContent}>
              <p><strong>What is your return policy?</strong></p>
              <p>We offer a 30-day return policy...</p>

              <p><strong>How do I track my order?</strong></p>
              <p>Once your order has been shipped, you will receive a tracking number...</p>
            </div>
          )}
        </div>

        {/* Accordion Item 2 */}
        <div style={styles.accordionItem}>
          <button
            style={styles.accordionButton}
            onClick={() => toggleAccordion(1)}
          >
            Shipping & Delivery
          </button>
          {activeIndex === 1 && (
            <div style={styles.accordionContent}>
              <p><strong>How long does shipping take?</strong></p>
              <p>Standard shipping typically takes 5-7 business days...</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '800px',
    margin: '50px auto',
    padding: '20px',
    backgroundColor: 'white',
    borderRadius: '8px',
    boxShadow: '0 0 15px rgba(0, 0, 0, 0.1)',
  },
  header: {
    textAlign: 'center',
    marginBottom: '20px',
    fontSize: '24px',
  },
  accordion: {
    display: 'flex',
    flexDirection: 'column',
  },
  accordionItem: {
    marginBottom: '10px',
  },
  accordionButton: {
    width: '100%',
    padding: '15px',
    textAlign: 'left',
    backgroundColor: '#333',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    fontSize: '18px',
    borderRadius: '4px',
    transition: 'background-color 0.3s ease',
  },
  accordionContent: {
    padding: '10px',
    backgroundColor: '#f9f9f9',
    display: 'block',
    borderLeft: '4px solid #333',
  },
};

export default Faqs;
